package com.github.adriianh.app

import com.github.adriianh.command.*
import com.github.adriianh.common.TaskManager
import com.github.ajalt.clikt.core.CliktCommand
import com.github.ajalt.clikt.core.main
import com.github.ajalt.clikt.core.subcommands

class ToDo : CliktCommand() {
    private val manager = TaskManager()

    override fun run() = Unit

    init {
        subcommands(
            FileCommand(manager),
            AddCommand(manager),
            DeleteCommand(manager),
            EditCommand(manager),
            DoneCommand(manager),
            ListCommand(manager)
        )
    }
}

fun main(args: Array<String>) = ToDo().main(args)